watch -n 0.1 'echo "CPU Temp     : $(vcgencmd measure_temp | sed '\''s/temp=//'\'' )\nGPU Temp     : $(echo "scale=2; $(cat /sys/class/thermal/thermal_zone0/temp) / 1000" | bc)Â°C\nCPU Freq     : $(($(vcgencmd measure_clock arm | awk -F= '\''{print $2}'\'' ) / 1000000)) MHz\nGPU Freq     : $(($(vcgencmd measure_clock core | awk -F= '\''{print $2}'\'' ) / 1000000)) MHz\nCore Voltage : $(vcgencmd measure_volts | awk -F= '\''{print $2}'\'' )\n\nRAM Usage:\n$(free -h)\nLoad Average : $(uptime | sed '\''s/.*load average: //'\'' )\n\nDisk Usage:\n$(df -h /)\nCPU Load     : $(top -bn1 | grep "Cpu(s)" | sed "s/.*, *\([0-9.]*\)%* id.*/\1/" | awk '\''{print 100 - $1}'\'' )%"'
